import React, { useEffect, useState } from 'react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const API_BASE = 'http://localhost:4000/api';

function App() {
  const [dates, setDates] = useState([]);
  const [selectedDate, setSelectedDate] = useState('');
  const [curve, setCurve] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Fetch dates
  useEffect(() => {
    setError('');
    fetch(`${API_BASE}/dates`)
      .then(async res => {
        if (!res.ok) throw new Error('Failed to fetch dates');
        return res.json();
      })
      .then(data => {
        console.log('Fetched dates:', data.dates);
        setDates(data.dates);
        if (data.dates && data.dates.length > 0) {
          setSelectedDate(data.dates[0]);
        } else {
          setError('No dates found.');
        }
      })
      .catch(err => {
        console.error('Error loading dates:', err);
        setError('Error loading dates: ' + err.message);
        setDates([]);
      });
  }, []);

  // Fetch curve for selected date
  useEffect(() => {
    if (!selectedDate) return;
    setLoading(true);
    setError('');
    fetch(`${API_BASE}/yield-curve?date=${encodeURIComponent(selectedDate)}`)
      .then(async res => {
        if (!res.ok) {
          const errJson = await res.json().catch(() => ({}));
          throw new Error(errJson.error || 'Failed to fetch yield curve');
        }
        return res.json();
      })
      .then(data => {
        setCurve(data.curve || []);
        setLoading(false);
      })
      .catch(err => {
        setError('Error loading yield curve: ' + err.message);
        setCurve([]);
        setLoading(false);
      });
  }, [selectedDate, dates]);

  // Helper to parse maturity label to months
  function maturityToMonths(label) {
    let l = label.trim().replace(/Month/i, 'Mo');
    if (/Mo/i.test(l)) {
      // e.g. "6 Mo", "1.5 Mo"
      return parseFloat(l) || 0;
    } else if (/YR/i.test(l)) {
      // e.g. "2 YR"
      return (parseFloat(l) || 0) * 12;
    }
    return 0;
  }

  // Accept all case/spacing variants for allowed maturities
  const allowedMaturities = [
    "6 Mo", "2 Yr", "5 Yr", "10 Yr", "20 Yr", "30 Yr"
  ];
  function normalizeMaturity(label) {
    return label.replace(/month/i, 'mo').replace(/yr/i, 'yr').replace(/\s+/g, ' ').trim().toLowerCase();
  }
  // For each allowed maturity, find the rate (if available), otherwise use null
  const allowedPoints = allowedMaturities.map(label => {
    const norm = normalizeMaturity(label);
    const pt = curve.find(p => normalizeMaturity(p.maturity) === norm);
    return {
      x: maturityToMonths(label),
      y: pt ? pt.rate : null,
      label
    };
  });
  // For Chart.js ticks
  const monthsToLabel = {};
  allowedPoints.forEach(pt => { monthsToLabel[pt.x] = pt.label; });
  const tickVals = allowedPoints.map(pt => pt.x);
  const minX = Math.min(...tickVals);
  const maxX = Math.max(...tickVals);

  // Only plot points with a y value
  const points = allowedPoints.filter(pt => pt.y != null);

  const chartData = {
    datasets: [
      {
        label: `Yield Curve (${selectedDate})`,
        data: points,
        fill: false,
        borderColor: 'rgb(75, 192, 192)',
        tension: 0.1,
        showLine: true,
        pointRadius: 5,
      },
    ],
  };


  // Chart.js v4 plugin to force custom x-axis ticks at allowed maturities
  const forcedXTicksPlugin = {
    id: 'forcedXTicks',
    beforeInit: (chart) => {
      const xAxis = chart.options.scales.x;
      xAxis.ticks = xAxis.ticks || {};
      xAxis.ticks.autoSkip = false;
      xAxis.ticks.callback = function(value) {
        return monthsToLabel[value] || '';
      };
      xAxis.min = minX;
      xAxis.max = maxX;
    },
    afterBuildTicks: (chart) => {
      const xScale = chart.scales.x;
      if (xScale) {
        // Overwrite the ticks to be exactly our allowed maturities
        xScale.ticks = tickVals.map(val => ({ value: val, label: monthsToLabel[val] }));
        xScale._ticks = xScale.ticks; // for Chart.js internal compatibility
      }
    }
  };

  return (
    <div style={{ maxWidth: 700, margin: '40px auto', fontFamily: 'sans-serif' }}>
      <h2>US Treasury Yield Curve Viewer</h2>
      {error && (
        <div style={{ color: 'red', marginBottom: 16 }}>
          <b>Error:</b> {error}
        </div>
      )}
      {dates.length === 0 ? (
        <div>No dates available.</div>
      ) : (
        <label>
          Select Date:{' '}
          <select
            value={selectedDate}
            onChange={e => setSelectedDate(e.target.value)}
            style={{ fontSize: '1rem', margin: '0 10px' }}
          >
            {dates.map(date => (
              <option key={date} value={date}>{date}</option>
            ))}
          </select>
        </label>
      )}
      {loading ? (
        <div>Loading chart...</div>
      ) : (
        <Line data={chartData} options={{
          responsive: true,
          plugins: {
            legend: { position: 'top' },
            title: { display: true, text: 'Yield Curve' },
          },
          scales: {
            x: {
              type: 'linear',
              title: {
                display: true,
                text: 'Maturity',
                font: { size: 14 }
              },
              min: minX,
              max: maxX,
              ticks: {
                autoSkip: false,
                maxRotation: 0,
                minRotation: 0,
                callback: function(value) {
                  return monthsToLabel[value] || '';
                }
              }
            },
            y: {
              beginAtZero: true,
              title: {
                display: true,
                text: 'Treasury Par Rate',
                font: { size: 14 }
              }
            }
          },
          plugins: [forcedXTicksPlugin]
        }} />
      )}
    </div>
  );
}

export default App;
