import React, { useEffect, useState } from 'react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const API_BASE = 'http://localhost:4000/api';

function App() {
  const [dates, setDates] = useState([]);
  const [selectedDate, setSelectedDate] = useState('');
  const [curve, setCurve] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Fetch dates
  useEffect(() => {
    setError('');
    fetch(`${API_BASE}/dates`)
      .then(async res => {
        if (!res.ok) throw new Error('Failed to fetch dates');
        return res.json();
      })
      .then(data => {
        console.log('Fetched dates:', data.dates);
        setDates(data.dates);
        if (data.dates && data.dates.length > 0) {
          setSelectedDate(data.dates[0]);
        } else {
          setError('No dates found.');
        }
      })
      .catch(err => {
        console.error('Error loading dates:', err);
        setError('Error loading dates: ' + err.message);
        setDates([]);
      });
  }, []);

  // Fetch curve for selected date
  useEffect(() => {
    if (!selectedDate) return;
    setLoading(true);
    setError('');
    fetch(`${API_BASE}/yield-curve?date=${encodeURIComponent(selectedDate)}`)
      .then(async res => {
        if (!res.ok) {
          const errJson = await res.json().catch(() => ({}));
          throw new Error(errJson.error || 'Failed to fetch yield curve');
        }
        return res.json();
      })
      .then(data => {
        setCurve(data.curve || []);
        setLoading(false);
      })
      .catch(err => {
        setError('Error loading yield curve: ' + err.message);
        setCurve([]);
        setLoading(false);
      });
  }, [selectedDate, dates]);

  const chartData = {
    labels: curve.map(pt => pt.maturity),
    datasets: [
      {
        label: `Yield Curve (${selectedDate})`,
        data: curve.map(pt => pt.rate),
        fill: false,
        borderColor: 'rgb(75, 192, 192)',
        tension: 0.1,
      },
    ],
  };

  return (
    <div style={{ maxWidth: 700, margin: '40px auto', fontFamily: 'sans-serif' }}>
      <h2>US Treasury Yield Curve Viewer</h2>
      {error && (
        <div style={{ color: 'red', marginBottom: 16 }}>
          <b>Error:</b> {error}
        </div>
      )}
      {dates.length === 0 ? (
        <div>No dates available.</div>
      ) : (
        <label>
          Select Date:{' '}
          <select
            value={selectedDate}
            onChange={e => setSelectedDate(e.target.value)}
            style={{ fontSize: '1rem', margin: '0 10px' }}
          >
            {dates.map(date => (
              <option key={date} value={date}>{date}</option>
            ))}
          </select>
        </label>
      )}
      {loading ? (
        <div>Loading chart...</div>
      ) : (
        <Line data={chartData} options={{
          responsive: true,
          plugins: {
            legend: { position: 'top' },
            title: { display: true, text: 'Yield Curve' },
          },
        }} />
      )}
    </div>
  );
}

export default App;
